// 1. Hole die riesigen Google Daten
const googleData = items[0].json;

// Check: Haben wir √ºberhaupt Daten?
if (!googleData.lighthouseResult) {
  return { error: "Keine Google Daten empfangen" };
}

const lh = googleData.lighthouseResult;
const audits = lh.audits;

// 2. Wir bauen ein "schlankes" Paket nur mit dem N√∂tigsten
const cleanData = {
  // Die Scores
  mobile_score: (lh.categories.performance.score * 100).toFixed(0),
  metrics: {
    LCP: audits['largest-contentful-paint'].displayValue,
    CLS: audits['cumulative-layout-shift'].displayValue,
    TBT: audits['total-blocking-time'].displayValue
  },

  // Die Top 5 Probleme (gefiltert & sortiert)
  top_issues: Object.values(audits)
    // Nur Audits nehmen, die Fehler sind (Score < 0.9) und Details haben
    .filter(a => a.score !== null && a.score < 0.9 && a.details && a.details.type === 'table')
    .sort((a, b) => (a.score || 1) - (b.score || 1)) // Schlimmste zuerst
    .slice(0, 5) // Nur Top 5
    .map(a => ({
      title: a.title,
      saving: a.displayValue,
      description: a.description.substring(0, 150) + "..." // Text k√ºrzen!
    }))
};

return cleanData;
